sys_script.script:
(function executeRule(current, previous /*null when async*/) {

/*
	gs.addInfoMessage("Current user's email address is: " + current.u_requested_for.email );

	gs.info('Test variable substitution: ' + '{0},{1},{2}'
			, current.u_requested_for
		    , current.u_requested_for.email
		    , current.number);
*/

	/*REQ: Prevent users from setting the What needed field value to a date in the past when creating NeedIt requests.

	Approach:
	1. getValue of u_when_needed field
	2. getValue of current date
	3. if u_when_needed date < current date, abort operation

	*/

	var when_needed = new GlideDateTime(current.u_when_needed);
	var now = new GlideDateTime();

	if(when_needed.before(now)){
	current.setAbortAction(true);
	gs.addErrorMessage('When needed should be greater than current date!');
	};


	/* REQ: Prevent users from submitting requests for today */

	gmt_needed = when_needed.getLocalDate();
	gmt_now = now.getLocalDate();

	if(gmt_needed.compareTo(gmt_now) == 0){
		gs.addErrorMessage('Please provide an allowance for date needed');
	}

	var int1 = 2;
	var int2 = 3;

	gs.addInfoMessage('The sum of 2 and 3 is called from a script include: ' + onDemand(int1, int2));

	// Pass the Requested for email to the Script Include.  Store the return
	// value from the Script Include in the isEmail variable
	var isEmail = validateEmailAddress(current.u_requested_for_email);
	// If isEmail is false (email address syntax is not valid) do not save
	// the record.  Write an error message to the screen.
	if(isEmail == false){
		gs.addErrorMessage(current.u_requested_for_email + " is not a valid email address.  You must provide a valid email address.");
		current.setAbortAction(true);
	}

})(current, previous);
