sys_script_include.script:
var dupTasks = Class.create();
dupTasks.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {

	dupTasks: function() {
		var dupRecords = [];
		var gaDupCheck1 = new GlideAggregate('task');
		gaDupCheck1.addAggregate('COUNT', 'number');
		gaDupCheck1.groupBy('number');
		gaDupCheck1.addHaving('COUNT', '>', 1);
		gaDupCheck1.query();
		while (gaDupCheck1.next()) {
			dupRecords.push(gaDupCheck1.number.toString());
		}
		return dupRecords;
	},

type: 'dupTasks'
});
